mapper.update:
update board(
	set title =  #{title},
		contents = #{contents}
where board_no = #{boardNo} and category_no = #{categoryNo}

mapper.delete:
	delete
	  from board
  	 where board_no = #{boardNo} and category_no = #{categoryNo}

mapper.select:
	select * 
	  from board
  	 where board_no = #{boardNo} and category_no = #{categoryNo}
